"use client"

import { useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Button } from "../components/ui/button"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "../components/ui/card"
import { Input } from "../components/ui/input"
import { Label } from "../components/ui/label"
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/table"
import { Loader2, Calendar as CalendarIcon } from "lucide-react"
import { Calendar } from "../components/ui/calendar"
import { Popover, PopoverContent, PopoverTrigger } from "../components/ui/popover"
import { format } from "date-fns"
import { cn } from "@/"

interface Task {
  name: string;
  deadline: Date;
  profit: number;
}

export default function TaskScheduler() {
  const [tasks, setTasks] = useState<Task[]>([])
  const [name, setName] = useState('')
  const [deadline, setDeadline] = useState<Date | undefined>(undefined)
  const [profit, setProfit] = useState('')
  const [scheduledTasks, setScheduledTasks] = useState<Task[]>([])
  const [totalProfit, setTotalProfit] = useState(0)
  const [loading, setLoading] = useState(false)

  const today = new Date()
  today.setHours(0, 0, 0, 0)

  const addTask = () => {
    if (name && deadline && profit) {
      setTasks([...tasks, { name, deadline, profit: parseInt(profit) }])
      setName('')
      setDeadline(undefined)
      setProfit('')
    }
  }

  const scheduleTasks = async () => {
    setLoading(true)
    try {
      const response = await fetch('http://localhost:5000/schedule_tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          tasks: tasks.map(task => ({
            ...task,
            deadline: Math.ceil((task.deadline.getTime() - new Date().getTime()) / (1000 * 3600 * 24))
          }))
        }),
      })
      const data = await response.json()
      setScheduledTasks(data.scheduled_tasks.map((task: any) => ({
        ...task,
        deadline: new Date(new Date().getTime() + task.deadline * 24 * 60 * 60 * 1000)
      })))
      setTotalProfit(data.total_profit)
    } catch (error) {
      console.error('Error al conectar con el servidor', error)
    }
    setLoading(false)
  }

  return (
    <Card className="w-[600px]">
      <CardHeader>
        <CardTitle className="text-3xl font-bold">Planificador de Tareas</CardTitle>
        <CardDescription>Maximiza las ganancias programando tareas dentro de sus fechas límite</CardDescription>
      </CardHeader>
      <CardContent>
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="grid w-full items-center gap-4"
        >
          <div className="flex flex-col space-y-1.5">
            <Label htmlFor="name">Nombre de la tarea</Label>
            <Input id="name" value={name} onChange={(e:any) => setName(e.target.value)} />
          </div>
          <div className="flex flex-col space-y-1.5">
            <Label htmlFor="deadline">Fecha límite</Label>
            <Popover>
              <PopoverTrigger asChild>
                <Button
                  id="deadline"
                  variant={"outline"}
                  className={cn(
                    "w-full justify-start text-left font-normal",
                    !deadline && "text-muted-foreground"
                  )}
                >
                  <CalendarIcon className="mr-2 h-4 w-4" />
                  {deadline ? format(deadline, "PPP") : <span>Selecciona una fecha</span>}
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-auto p-0">
                <Calendar
                  mode="single"
                  selected={deadline}
                  onSelect={setDeadline}
                  initialFocus
                  fromDate={today}
                />
              </PopoverContent>
            </Popover>
          </div>
          <div className="flex flex-col space-y-1.5">
            <Label htmlFor="profit">Ganancia</Label>
            <Input id="profit" type="number" min="0" value={profit} onChange={(e:any) => setProfit(e.target.value)} />
          </div>
          <Button 
            onClick={addTask}
            className="transition-all duration-200 hover:scale-105"
          >
            Agregar Tarea
          </Button>
        </motion.div>
        <AnimatePresence>
          {tasks.length > 0 && (
            <motion.div 
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.5 }}
              className="mt-4 overflow-hidden"
            >
              <h3 className="text-lg font-semibold">Tareas Agregadas:</h3>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Nombre</TableHead>
                    <TableHead>Fecha límite</TableHead>
                    <TableHead>Ganancia</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {tasks.map((task, index) => (
                    <motion.tr
                      key={index}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.3, delay: index * 0.1 }}
                    >
                      <TableCell>{task.name}</TableCell>
                      <TableCell>{format(task.deadline, "PPP")}</TableCell>
                      <TableCell>${task.profit}</TableCell>
                    </motion.tr>
                  ))}
                </TableBody>
              </Table>
            </motion.div>
          )}
        </AnimatePresence>
      </CardContent>
      <CardFooter className="flex flex-col items-start">
        <Button 
          onClick={scheduleTasks} 
          disabled={loading || tasks.length === 0}
          className="transition-all duration-200 hover:scale-105"
        >
          {loading ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Planificando...
            </>
          ) : (
            'Planificar Tareas'
          )}
        </Button>
        <AnimatePresence>
          {scheduledTasks.length > 0 && (
            <motion.div 
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.5 }}
              className="mt-4 w-full overflow-hidden"
            >
              <h3 className="text-lg font-semibold">Tareas Planificadas:</h3>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Nombre</TableHead>
                    <TableHead>Fecha límite</TableHead>
                    <TableHead>Ganancia</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {scheduledTasks.map((task, index) => (
                    <motion.tr
                      key={index}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.3, delay: index * 0.1 }}
                    >
                      <TableCell>{task.name}</TableCell>
                      <TableCell>{format(new Date(task.deadline), "PPP")}</TableCell>
                      <TableCell>${task.profit}</TableCell>
                    </motion.tr>
                  ))}
                </TableBody>
              </Table>
              <motion.p 
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.3 }}
                className="mt-4 font-semibold text-2xl"
              >
                Ganancia Total: ${totalProfit}
              </motion.p>
            </motion.div>
          )}
        </AnimatePresence>
      </CardFooter>
      <div className="text-center text-sm text-gray-500 mt-8">
        <p>Universidad Tecnológica de Bolivar - Proyecto realizado por:</p>
        <p>José Fernando González Ortiz</p>
        <p>Isabella Sofía Arrieta Guardo</p>
        <p>Daniel David Herrera Acevedo</p>
      </div>
    </Card>
  )
}
